import React, { useState, useEffect } from "react";
import {
  BrowserRouter as Router,
  Route,
  Routes,
  Link,
  useNavigate,
} from "react-router-dom";
import { useAuth0 } from "@auth0/auth0-react";
import "bootstrap/dist/css/bootstrap.min.css";
import "./App.css";

function App() {
  const [examTaken, setExamTaken] = useState(false);
  const [registrationMessage, setRegistrationMessage] = useState(false);
    const [examScore, setExamScore] = useState(null); // New state for exam score
  const { loginWithRedirect, logout, user, isAuthenticated, isLoading } =
    useAuth0();

  const handleRegisterClick = () => {
    if (examTaken) {
      setRegistrationMessage(true);
    } else {
      window.location.href =
        "https://quizfrompdf-5hj9imjrixfnoadmp8wjtf.streamlit.app/";
    }
  };

  const cardsData = [
    {
      name: "History and Organization of NCC",
      description:
        "This topic covers the origins, evolution, and structure of the National Cadet Corps. It includes information about the establishment of NCC, its objectives, organizational hierarchy, and the roles and responsibilities of different NCC units.",
      link: "https://www.udemy.com/share/1013Aw/",
      image:
        "data:image/jpeg;base64,",
    },
    {
      name: "Drill and Ceremonial Procedures",
      description:
        "Cadets learn about various drill movements, commands, and ceremonial procedures. This topic includes the basics of foot drills, rifle drills, saluting, and the significance of different ceremonial parades and events within the NCC.",
      link: "https://www.udemy.com/share/101sYK/",
      image:
        "data:image/jpeg;base64,",
    },
    {
      name: "Leadership and Personality Development",
      description:
        "Focuses on the qualities of a good leader, leadership styles, and methods for developing leadership skills. This topic also includes discussions on personal grooming, communication skills, and the importance of discipline and teamwork in shaping a cadet's personality.",
      link: "https://www.udemy.com/share/101vLm/",
      image:
        "data:image/png;base64,",
    },
    {
      name: "National Integration and Awareness",
      description:
        "Covers the importance of national integration, the role of NCC in promoting unity, and the cultural diversity of India. This topic includes knowledge about major national events, symbols, and significant contributions of NCC to nation-building and social service activities.",
      link: "https://www.udemy.com/share/105wSU/",
      image:
        "data:image/jpeg;base64,",
    },
  ];
  useEffect(() => {
    // Fetch the exam score from the backend
    fetch("http://localhost:3000/exam-score")
      .then((response) => response.json())
      .then((data) => {
        console.log(data);
        setExamScore(data.percentage);
      })
      .catch((error) => console.error("Error fetching exam score:", error));
  }, []);
  useEffect(() => {
    // Embed the script when the component mounts
    const script1 = document.createElement("script");
    script1.innerHTML = `
      window.embeddedChatbotConfig = {
        chatbotId: "Fop0l9FyT5HqGIwRkdvmX",
        domain: "www.chatbase.co"
      }
    `;
    document.body.appendChild(script1);

    const script2 = document.createElement("script");
    script2.src = "https://www.chatbase.co/embed.min.js";
    script2.setAttribute("chatbotId", "Fop0l9FyT5HqGIwRkdvmX");
    script2.setAttribute("domain", "www.chatbase.co");
    script2.defer = true;
    document.body.appendChild(script2);

    // Cleanup the scripts when the component unmounts
    return () => {
      document.body.removeChild(script1);
      document.body.removeChild(script2);
    };
  }, []);

  return (
    <Router>
      <div className="App">
        <nav className="navbar navbar-expand-lg navbar-light bg-light">
          <div className="container-fluid">
            <Link className="navbar-brand" to="/">
              Trainee
            </Link>
            <button
              className="navbar-toggler"
              type="button"
              data-bs-toggle="collapse"
              data-bs-target="#navbarNav"
              aria-controls="navbarNav"
              aria-expanded="false"
              aria-label="Toggle navigation"
            >
              <span className="navbar-toggler-icon"></span>
            </button>
            <div className="collapse navbar-collapse" id="navbarNav">
              <ul className="navbar-nav ms-auto">
                {isAuthenticated ? (
                  <>
                    <li className="nav-item">
                      <div className="nav-link">
                        <img
                          src={user.picture}
                          alt={user.name}
                          className="rounded-circle me-2"
                          style={{ width: "30px" }}
                        />
                        <span>{user.name}</span>
                      </div>
                    </li>
                    <li className="nav-item">
                      <button
                        className="btn btn-primary me-2"
                        onClick={handleRegisterClick}
                      >
                        EXAM
                      </button>
                    </li>
                    <li className="nav-item">
                      <button
                        className="btn btn-secondary"
                        onClick={() =>
                          logout({ returnTo: window.location.origin })
                        }
                      >
                        Log Out
                      </button>
                    </li>
                  </>
                ) : (
                  <li className="nav-item">
                    <button
                      className="btn btn-primary"
                      onClick={() => loginWithRedirect()}
                    >
                      Login
                    </button>
                  </li>
                )}
              </ul>
            </div>
          </div>
        </nav>
        <Routes>
          <Route
            path="/exam"
            element={<ExamPage setExamTaken={setExamTaken} setExamScore={setExamScore} />}
          />
          <Route
            path="/"
            element={
              <>
                <div className="container mt-4">
                  <div className="row">
                    {/* {cardsData.map((card, index) => (
                      <GreetingCard
                        key={index}
                        name={card.name}
                        description={card.description}
                        link={card.link}
                        image={card.image}
                      />
                    ))} */}
                    {examScore !== null
                      ? examScore > 60
                        ? cardsData.slice(0, 2).map((card, index) => (
                            <GreetingCard
                              key={index}
                              name={card.name}
                              description={card.description}
                              link={card.link}
                              image={card.image}
                            />
                          ))
                        : cardsData.slice(2, 4).map((card, index) => (
                            <GreetingCard
                              key={index}
                              name={card.name}
                              description={card.description}
                              link={card.link}
                              image={card.image}
                            />
                          ))
                      : cardsData.map((card, index) => (
                          <GreetingCard
                            key={index}
                            name={card.name}
                            description={card.description}
                            link={card.link}
                            image={card.image}
                          />
                        ))}
                  </div>
                  {registrationMessage && (
                    <div className="alert alert-success mt-4">
                      <h4 className="alert-heading">Response submitted</h4>
                      <p>You have successfully registered for the camp!</p>
                    </div>
                  )}
                </div>
              </>
            }
          />
        </Routes>
      </div>
    </Router>
  );
}

function GreetingCard({ name, description, link, image }) {
  return (
    <div className="col-md-6 mb-3">
      <div className="card h-100">
        <img src={image} className="card-img-top" alt={name} />
        <div className="card-body">
          <h5 className="card-title">{name}</h5>
          <p className="card-text">{description}</p>
        </div>
        <div className="card-footer">
          <a
            href={link}
            className="btn btn-primary"
            target="_blank"
            rel="noopener noreferrer"
          >
            Go to Course
          </a>
        </div>
      </div>
    </div>
  );
}

function ExamPage({ setExamTaken, setExamScore}) {
  const navigate = useNavigate();

  const handleExamSubmit = () => {

    const score = Math.floor(Math.random() * 100); // Random score for demonstration
    setExamScore(score);

    setExamTaken(true);
    alert("You have successfully completed the exam!");
    navigate("/");
  };

  return (
    <div className="container mt-6">
      <h2>Exam Page</h2>
      <p>Please complete the exam to proceed with the registration.</p>
      <button className="btn btn-success" onClick={handleExamSubmit}>
        Submit Exam
      </button>
    </div>
  );
}

export default App;
